<script src="//ajax.aspnetcdn.com/ajax/jquery.validate/1.9/jquery.validate.min.js"></script>  
<script type="text/javascript"> 

jQuery().ready(function() 
{
    var v = jQuery("#course_enq_form").validate({
      rules: {
            name: {
              required: true,
            },
            email: {
              required: true,
              email: true,
            },
            phone: {
              required: true,
              number: true,
            },
            course: {
              required: true,
            },
            liphone: {
              required: true,
            },
            licategory: {
              required: true,
            },
            lidescription: {
              required: true,
            },        
                       
      },
      
      errorPlacement: function(){
        return false; //change border color + no text
      },
        
      errorElement: "span",
      errorClass: "help-inline-error",

    });

    $('body').on('click',"#course_enq_submit",function(){
        if (v.form()) 
        {
            var mydata=$('#course_enq_form').serialize();
	        $.ajax({
				type: "POST",
				url: '<?php echo site_url('home/inquiry'); ?>',
				data: mydata,    
				success: function(data)
				{   
					$('#enq_success').show();  
					setTimeout(function() {
			          $("#enq_success").fadeOut();
			        },5000);
			        document.getElementById("course_enq_form").reset();
				}
	        });
         	return false;   
        }
    });

  });
  </script>


